pragma solidity ^0.5.0;

import "./RecurringTransaction.sol";
import "./Interfaces.sol";

/// @title RecurringTransactionFactory Contract
/// @author Joseph Reed
/// @dev This contract's goal is to create a recurring transactions and optionally
///      to start it if the factory is set as a delegate
contract RecurringTransactionFactory is CloneFactory {

    uint public blockCreated = block.number;  // The block this contract was deployed
    address public blueprint;                 // The recurring transaction blueprint used by the clone factory
    uint public totalTransactionsCreated = 0;       // The number of recurring transaction by this factory

    /// @notice Constructor to create a Recurring Transaction Factory
    /// @param _blueprint The recurring transaction blueprint
    constructor (address _blueprint) public {
        blueprint = _blueprint;
    }

    /// @notice Creates a recurring transaction smart contract
    /// @param wallet The wallet from which to pull future transaction costs
    /// @param delegate The delegate that pulls future transaction costs
    /// @param factory The source for transaction requests
    /// @return The recurring transaction contract address
    function createRecurringTransaction (
        IDelegatedWallet wallet,
        ITransactionDelegate delegate,
        ITransactionRequestFactory factory
    ) public returns (IRecurringTransaction) {
        return newRTX(wallet, delegate, factory);
    }

    /// @notice Creates a recurring transaction smart contract
    /// @param wallet The wallet from which to pull future transaction costs
    /// @param delegate The delegate that pulls future transaction costs
    /// @param factory The source for transaction requests
    /// @param callAddress todo
    /// @param callData todo
    /// @param callOptions todo
    /// @param txRequestOptions todo
    /// @return The recurring transaction contract address
    function createAndStartRecurringTransaction (
        IDelegatedWallet wallet,
        ITransactionDelegate delegate,
        ITransactionRequestFactory factory,
        address callAddress,
        bytes memory callData,
        uint[4] memory callOptions,
        uint[7] memory txRequestOptions
    ) public returns (IRecurringTransaction) {
        RecurringTransaction transaction = newRTX(wallet, delegate, factory);
        delegate.schedule(transaction);
        transaction.start(callAddress, callData, callOptions, txRequestOptions);
        return transaction;
    }

    function newRTX (
        IDelegatedWallet wallet,
        ITransactionDelegate delegate,
        ITransactionRequestFactory factory
    ) internal returns (RecurringTransaction) {
        // for converting to payable addresses see:
        // https://solidity.readthedocs.io/en/v0.5.0/050-breaking-changes.html?highlight=address_make_payable
        address payable clone = address(uint160(createClone(blueprint)));
        RecurringTransaction transaction = RecurringTransaction(clone);
        transaction.initialize(wallet, delegate, factory);
        emit CreateRecurringTransaction_event(msg.sender, transaction);
        totalTransactionsCreated++;
        return transaction;
    }

    event CreateRecurringTransaction_event(address indexed caller, RecurringTransaction transaction);

}

