pragma solidity ^0.5.0;

import "./external/RequestFactoryInterface.sol";
import "./Interfaces.sol";

/// @title Transaction Request Factory
/// @author Joseph Reed
/// @dev This contract creates a request to execute a future transaction
contract TransactionRequestFactory is ITransactionRequestFactory, Owned {

    RequestFactoryInterface public EAC;       // The Ethereum Alarm Clock creates decentralized alarms
    address payable public donationWallet;    // The wallet that collects donations from scheduled transactions
    uint public blockCreated = block.number;  // The block this factory was created
    uint public requestsCreated = 0;          // Tracks the number of requests created by this factory

    /// @notice Constructor to create a future transaction factory
    /// @param _EAC The contract responsible for deploying decentralized alarms
    constructor (RequestFactoryInterface _EAC) public {
        EAC = _EAC;
        donationWallet = msg.sender;
    }

    function createTransactionRequest (address wallet, uint[7] memory options) public payable returns (ITransactionRequest) {
        uint deposit = options[6];
        require(msg.value == deposit, "msg value should equal the deposit");
        uint donation = deposit / 100;
        uint timeBounty = deposit - donation;

        address[3] memory addressOptions = [
            wallet,         // Owner of the transaction request and the change address
            donationWallet, // The donation is sent to the donation wallet
            msg.sender      // The contract to call at execution
        ];

        uint[12] memory uintOptions = [
            donation,       // A donation that goes to the donation wallet
            timeBounty,     // The bounty for the account that triggers this transaction
            options[0],     // The claim window size
            options[1],     // The freeze interval duration
            options[2],     // The reserved window size
            2,              // 1 = use block based scheduling, 2 = Use timestamp based scheduling
            options[3],     // The size of the execution window
            options[4],     // The start of the execution window
            options[5],     // The amount of gas to call the transaction with
            0,              // The amount of ether to be sent with the transaction
            0,              // The minimum gas price for the transaction when called
            deposit         // The required deposit by the claimer
        ];

        address eacTxRequest = EAC.createValidatedRequest.value(msg.value)(addressOptions, uintOptions, "");
        ITransactionRequest txRequest = ITransactionRequest(eacTxRequest);
        requestsCreated++;
        emit TransactionRequest_event (msg.sender, txRequest);
        return txRequest;
    }

    function setDonationWallet (address payable newDonationWallet) public onlyOwner {
      require(newDonationWallet != address(0x0), "new wallet cannot be 0x0");
      donationWallet = newDonationWallet;
    }

    event TransactionRequest_event(address indexed caller, ITransactionRequest txRequest);
    event InvalidRequest_event (bool[6] reasons);

}

